import React, { useState, useEffect } from 'react';
import { Cliente, ClienteFormData } from './types/Cliente';
import { ClienteList } from './components/ClienteList';
import { ClienteForm } from './components/ClienteForm';
import { clienteService } from './services/clienteService';

function App() {
    const [clientes, setClientes] = useState<Cliente[]>([]);
    const [selectedCliente, setSelectedCliente] = useState<Cliente | undefined>();
    const [isFormVisible, setIsFormVisible] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [loading, setLoading] = useState(true);
    const [testResult, setTestResult] = useState<string | null>(null);

    const loadClientes = async () => {
        try {
            setLoading(true);
            console.log('ðŸš€ Iniciando carregamento de clientes...');
            const data = await clienteService.getAll();
            setClientes(data);
            setError(null);
            console.log('âœ… Clientes carregados com sucesso:', data);
        } catch (err) {
            console.error('âŒ Erro ao carregar clientes:', err);
            setError('Erro ao carregar clientes. Verifique se o backend estÃ¡ rodando na porta 32831.');
        } finally {
            setLoading(false);
        }
    };

    const testBackendConnection = async () => {
        try {
            setTestResult('ðŸ”„ Testando conexÃ£o...');
            console.log('ðŸ§ª Iniciando teste de conexÃ£o com o backend...');

            const data = await clienteService.getAll();
            const result = `âœ… ConexÃ£o OK! Recebidos ${data.length} cliente(s)`;
            setTestResult(result);
            console.log('âœ… Teste de conexÃ£o bem-sucedido:', data);
        } catch (err) {
            const errorMsg = `âŒ Erro na conexÃ£o: ${err instanceof Error ? err.message : 'Erro desconhecido'}`;
            setTestResult(errorMsg);
            console.error('âŒ Teste de conexÃ£o falhou:', err);
        }
    };

    useEffect(() => {
        loadClientes();
    }, []);

    const handleCreate = async (data: ClienteFormData) => {
        try {
            await clienteService.create(data);
            setIsFormVisible(false);
            loadClientes();
            setError(null);
        } catch (err) {
            setError('Erro ao criar cliente');
        }
    };

    const handleUpdate = async (data: ClienteFormData) => {
        if (!selectedCliente) return;
        try {
            await clienteService.update(selectedCliente.id!, data);
            setSelectedCliente(undefined);
            setIsFormVisible(false);
            loadClientes();
            setError(null);
        } catch (err) {
            setError('Erro ao atualizar cliente');
        }
    };

    const handleDelete = async (id: number) => {
        if (!window.confirm('Tem certeza que deseja excluir este cliente?')) return;
        try {
            await clienteService.delete(id);
            loadClientes();
            setError(null);
        } catch (err) {
            setError('Erro ao excluir cliente');
        }
    };

    const handleEdit = (cliente: Cliente) => {
        setSelectedCliente(cliente);
        setIsFormVisible(true);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
            <div className="container mx-auto px-4 py-8">
                <div className="max-w-7xl mx-auto">
                    {/* Header */}
                    <div className="text-center mb-8">
                        <h1 className="text-4xl font-bold text-gray-900 mb-2">
                            Sistema de Gerenciamento de Clientes
                        </h1>
                        <p className="text-gray-600">
                            Gerencie seus clientes de forma simples e eficiente
                        </p>
                    </div>

                    {/* Test Connection Button */}
                    <div className="mb-6 text-center">
                        <button
                            onClick={testBackendConnection}
                            className="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700 mr-4"
                        >
                            ðŸ§ª Testar ConexÃ£o Backend
                        </button>
                        {testResult && (
                            <div className={`mt-2 p-2 rounded-md ${
                                testResult.includes('âœ…') ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                            }`}>
                                {testResult}
                            </div>
                        )}
                    </div>

                    {/* Error Message */}
                    {error && (
                        <div className="mb-6 p-4 bg-red-100 border border-red-400 text-red-700 rounded-lg">
                            <div className="flex items-center">
                                <svg className="w-5 h-5 mr-2" fill="currentColor" viewBox="0 0 20 20">
                                    <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                                </svg>
                                {error}
                            </div>
                        </div>
                    )}

                    {/* Main Content */}
                    <div className="bg-white rounded-lg shadow-lg overflow-hidden">
                        {isFormVisible ? (
                            <div className="p-6">
                                <div className="flex justify-between items-center mb-6">
                                    <h2 className="text-2xl font-semibold text-gray-900">
                                        {selectedCliente ? 'Editar Cliente' : 'Novo Cliente'}
                                    </h2>
                                    <button
                                        onClick={() => {
                                            setIsFormVisible(false);
                                            setSelectedCliente(undefined);
                                        }}
                                        className="text-gray-400 hover:text-gray-600"
                                    >
                                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                                        </svg>
                                    </button>
                                </div>
                                <ClienteForm
                                    cliente={selectedCliente}
                                    onSubmit={selectedCliente ? handleUpdate : handleCreate}
                                    onCancel={() => {
                                        setIsFormVisible(false);
                                        setSelectedCliente(undefined);
                                    }}
                                />
                            </div>
                        ) : (
                            <div className="p-6">
                                <div className="flex justify-between items-center mb-6">
                                    <div>
                                        <h2 className="text-2xl font-semibold text-gray-900">Lista de Clientes</h2>
                                        <p className="text-gray-600 mt-1">
                                            {clientes.length} cliente{clientes.length !== 1 ? 's' : ''} encontrado{clientes.length !== 1 ? 's' : ''}
                                        </p>
                                    </div>
                                    <button
                                        onClick={() => setIsFormVisible(true)}
                                        className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                                    >
                                        <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                                        </svg>
                                        Novo Cliente
                                    </button>
                                </div>

                                {loading ? (
                                    <div className="flex justify-center items-center py-12">
                                        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
                                        <span className="ml-2 text-gray-600">Carregando clientes...</span>
                                    </div>
                                ) : (
                                    <ClienteList
                                        clientes={clientes}
                                        onEdit={handleEdit}
                                        onDelete={handleDelete}
                                    />
                                )}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
