import React, { useState, useEffect } from 'react';
import { Cliente, ClienteFormData, Endereco, Telefone } from '../types/Cliente';

interface ClienteFormProps {
    cliente?: Cliente;
    onSubmit: (data: ClienteFormData) => void;
    onCancel: () => void;
}

export const ClienteForm: React.FC<ClienteFormProps> = ({ cliente, onSubmit, onCancel }) => {
    const [formData, setFormData] = useState<ClienteFormData>({
        nome: '',
        email: '',
        enderecos: [{
            estado: '',
            cidade: '',
            bairro: '',
            rua: '',
            numero: '',
            codigoPostal: ''
        }],
        telefones: [{ ddd: '', numero: '' }]
    });

    useEffect(() => {
        if (cliente) {
            // Converter endereço único para array (compatibilidade com backend)
            let enderecosArray: Endereco[] = [];
            if (cliente.enderecos && cliente.enderecos.length > 0) {
                enderecosArray = cliente.enderecos;
            } else if (cliente.endereco) {
                enderecosArray = [cliente.endereco];
            } else {
                enderecosArray = [{
                    estado: '',
                    cidade: '',
                    bairro: '',
                    rua: '',
                    numero: '',
                    codigoPostal: ''
                }];
            }

            setFormData({
                nome: cliente.nome,
                email: cliente.email,
                enderecos: enderecosArray,
                telefones: cliente.telefones && cliente.telefones.length > 0 
                    ? cliente.telefones 
                    : [{ ddd: '', numero: '' }]
            });
        }
    }, [cliente]);

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSubmit(formData);
    };

    // Funções para endereços
    const addEndereco = () => {
        setFormData(prev => ({
            ...prev,
            enderecos: [...prev.enderecos, {
                estado: '',
                cidade: '',
                bairro: '',
                rua: '',
                numero: '',
                codigoPostal: ''
            }]
        }));
    };

    const removeEndereco = (index: number) => {
        if (formData.enderecos.length > 1) {
            setFormData(prev => ({
                ...prev,
                enderecos: prev.enderecos.filter((_, i) => i !== index)
            }));
        }
    };

    const updateEndereco = (index: number, field: keyof Endereco, value: string) => {
        setFormData(prev => ({
            ...prev,
            enderecos: prev.enderecos.map((endereco, i) => 
                i === index ? { ...endereco, [field]: value } : endereco
            )
        }));
    };

    // Funções para telefones
    const addTelefone = () => {
        setFormData(prev => ({
            ...prev,
            telefones: [...prev.telefones, { ddd: '', numero: '' }]
        }));
    };

    const removeTelefone = (index: number) => {
        if (formData.telefones.length > 1) {
            setFormData(prev => ({
                ...prev,
                telefones: prev.telefones.filter((_, i) => i !== index)
            }));
        }
    };

    const updateTelefone = (index: number, field: keyof Telefone, value: string) => {
        setFormData(prev => ({
            ...prev,
            telefones: prev.telefones.map((telefone, i) => 
                i === index ? { ...telefone, [field]: value } : telefone
            )
        }));
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-6">
            {/* Informações Pessoais */}
            <div className="bg-gray-50 p-4 rounded">
                <h3 className="text-lg font-semibold mb-4">Informações Pessoais</h3>
                <div className="space-y-4">
                    <div>
                        <label className="block text-sm font-medium mb-1">Nome:</label>
                        <input
                            type="text"
                            value={formData.nome}
                            onChange={(e) => setFormData({...formData, nome: e.target.value})}
                            required
                            className="w-full border border-gray-300 px-3 py-2 rounded"
                        />
                    </div>

                    <div>
                        <label className="block text-sm font-medium mb-1">Email:</label>
                        <input
                            type="email"
                            value={formData.email}
                            onChange={(e) => setFormData({...formData, email: e.target.value})}
                            required
                            className="w-full border border-gray-300 px-3 py-2 rounded"
                        />
                    </div>
                </div>
            </div>

            {/* Endereços */}
            <div className="bg-gray-50 p-4 rounded">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-semibold">Endereços</h3>
                    <button
                        type="button"
                        onClick={addEndereco}
                        className="bg-green-500 text-white px-3 py-1 rounded text-sm"
                    >
                        + Adicionar Endereço
                    </button>
                </div>

                {formData.enderecos.map((endereco, index) => (
                    <div key={index} className="border border-gray-300 rounded p-4 mb-4">
                        <div className="flex justify-between items-center mb-3">
                            <h4 className="font-medium">Endereço {index + 1}</h4>
                            {formData.enderecos.length > 1 && (
                                <button
                                    type="button"
                                    onClick={() => removeEndereco(index)}
                                    className="bg-red-500 text-white px-2 py-1 rounded text-sm"
                                >
                                    Remover
                                </button>
                            )}
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm font-medium mb-1">Estado:</label>
                                <input
                                    type="text"
                                    value={endereco.estado}
                                    onChange={(e) => updateEndereco(index, 'estado', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">Cidade:</label>
                                <input
                                    type="text"
                                    value={endereco.cidade}
                                    onChange={(e) => updateEndereco(index, 'cidade', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">Bairro:</label>
                                <input
                                    type="text"
                                    value={endereco.bairro}
                                    onChange={(e) => updateEndereco(index, 'bairro', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">Rua:</label>
                                <input
                                    type="text"
                                    value={endereco.rua}
                                    onChange={(e) => updateEndereco(index, 'rua', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">Número:</label>
                                <input
                                    type="text"
                                    value={endereco.numero}
                                    onChange={(e) => updateEndereco(index, 'numero', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">CEP:</label>
                                <input
                                    type="text"
                                    value={endereco.codigoPostal}
                                    onChange={(e) => updateEndereco(index, 'codigoPostal', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Telefones */}
            <div className="bg-gray-50 p-4 rounded">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-semibold">Telefones</h3>
                    <button
                        type="button"
                        onClick={addTelefone}
                        className="bg-green-500 text-white px-3 py-1 rounded text-sm"
                    >
                        + Adicionar Telefone
                    </button>
                </div>

                {formData.telefones.map((telefone, index) => (
                    <div key={index} className="border border-gray-300 rounded p-4 mb-4">
                        <div className="flex justify-between items-center mb-3">
                            <h4 className="font-medium">Telefone {index + 1}</h4>
                            {formData.telefones.length > 1 && (
                                <button
                                    type="button"
                                    onClick={() => removeTelefone(index)}
                                    className="bg-red-500 text-white px-2 py-1 rounded text-sm"
                                >
                                    Remover
                                </button>
                            )}
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm font-medium mb-1">DDD:</label>
                                <input
                                    type="text"
                                    value={telefone.ddd}
                                    onChange={(e) => updateTelefone(index, 'ddd', e.target.value)}
                                    required
                                    maxLength={2}
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                            <div>
                                <label className="block text-sm font-medium mb-1">Número:</label>
                                <input
                                    type="text"
                                    value={telefone.numero}
                                    onChange={(e) => updateTelefone(index, 'numero', e.target.value)}
                                    required
                                    className="w-full border border-gray-300 px-3 py-2 rounded"
                                />
                            </div>
                        </div>
                    </div>
                ))}
            </div>

            {/* Botões de ação */}
            <div className="flex gap-2">
                <button
                    type="submit"
                    className="bg-green-500 text-white px-4 py-2 rounded"
                >
                    {cliente ? 'Atualizar' : 'Criar'}
                </button>
                <button
                    type="button"
                    onClick={onCancel}
                    className="bg-gray-500 text-white px-4 py-2 rounded"
                >
                    Cancelar
                </button>
            </div>
        </form>
    );
};
