import React, { useState, useEffect } from 'react';
import { Cliente, ClienteFormData, Endereco, Telefone } from '../types/Cliente';

interface ClienteFormProps {
    cliente?: Cliente;
    onSubmit: (data: ClienteFormData) => void;
    onCancel: () => void;
}

export const ClienteForm: React.FC<ClienteFormProps> = ({ cliente, onSubmit, onCancel }) => {
    const [formData, setFormData] = useState<ClienteFormData>({
        nome: '',
        nomeSocial: '',
        email: '',
        endereco: {
            estado: '',
            cidade: '',
            bairro: '',
            rua: '',
            numero: '',
            codigoPostal: '',
            informacoesAdicionais: ''
        },
        telefones: [{ ddd: '', numero: '' }]
    });

    useEffect(() => {
        if (cliente) {
            setFormData({
                nome: cliente.nome,
                nomeSocial: cliente.nomeSocial || '',
                email: cliente.email,
                endereco: cliente.endereco || {
                    estado: '',
                    cidade: '',
                    bairro: '',
                    rua: '',
                    numero: '',
                    codigoPostal: '',
                    informacoesAdicionais: ''
                },
                telefones: cliente.telefones && cliente.telefones.length > 0 
                    ? cliente.telefones 
                    : [{ ddd: '', numero: '' }]
            });
        }
    }, [cliente]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleEnderecoChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            endereco: {
                ...prev.endereco,
                [name]: value
            }
        }));
    };

    const handleTelefoneChange = (index: number, field: keyof Telefone, value: string) => {
        setFormData(prev => ({
            ...prev,
            telefones: prev.telefones.map((tel, i) => 
                i === index ? { ...tel, [field]: value } : tel
            )
        }));
    };

    const addTelefone = () => {
        setFormData(prev => ({
            ...prev,
            telefones: [...prev.telefones, { ddd: '', numero: '' }]
        }));
    };

    const removeTelefone = (index: number) => {
        if (formData.telefones.length > 1) {
            setFormData(prev => ({
                ...prev,
                telefones: prev.telefones.filter((_, i) => i !== index)
            }));
        }
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onSubmit(formData);
    };

    return (
        <form onSubmit={handleSubmit} className="space-y-6 bg-white p-6 rounded-lg shadow-md">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                    <label htmlFor="nome" className="block text-sm font-medium text-gray-700">Nome *</label>
                    <input
                        type="text"
                        id="nome"
                        name="nome"
                        value={formData.nome}
                        onChange={handleChange}
                        required
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    />
                </div>
                <div>
                    <label htmlFor="nomeSocial" className="block text-sm font-medium text-gray-700">Nome Social</label>
                    <input
                        type="text"
                        id="nomeSocial"
                        name="nomeSocial"
                        value={formData.nomeSocial}
                        onChange={handleChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    />
                </div>
            </div>

            <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700">Email *</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                />
            </div>

            {/* Endereço */}
            <div className="border-t pt-4">
                <h3 className="text-lg font-medium text-gray-900 mb-4">Endereço</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                        <label htmlFor="estado" className="block text-sm font-medium text-gray-700">Estado *</label>
                        <input
                            type="text"
                            id="estado"
                            name="estado"
                            value={formData.endereco.estado}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                    <div>
                        <label htmlFor="cidade" className="block text-sm font-medium text-gray-700">Cidade *</label>
                        <input
                            type="text"
                            id="cidade"
                            name="cidade"
                            value={formData.endereco.cidade}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                    <div>
                        <label htmlFor="bairro" className="block text-sm font-medium text-gray-700">Bairro *</label>
                        <input
                            type="text"
                            id="bairro"
                            name="bairro"
                            value={formData.endereco.bairro}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                    <div>
                        <label htmlFor="rua" className="block text-sm font-medium text-gray-700">Rua *</label>
                        <input
                            type="text"
                            id="rua"
                            name="rua"
                            value={formData.endereco.rua}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                    <div>
                        <label htmlFor="numero" className="block text-sm font-medium text-gray-700">Número *</label>
                        <input
                            type="text"
                            id="numero"
                            name="numero"
                            value={formData.endereco.numero}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                    <div>
                        <label htmlFor="codigoPostal" className="block text-sm font-medium text-gray-700">CEP *</label>
                        <input
                            type="text"
                            id="codigoPostal"
                            name="codigoPostal"
                            value={formData.endereco.codigoPostal}
                            onChange={handleEnderecoChange}
                            required
                            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                        />
                    </div>
                </div>
                <div className="mt-4">
                    <label htmlFor="informacoesAdicionais" className="block text-sm font-medium text-gray-700">Informações Adicionais</label>
                    <input
                        type="text"
                        id="informacoesAdicionais"
                        name="informacoesAdicionais"
                        value={formData.endereco.informacoesAdicionais}
                        onChange={handleEnderecoChange}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                    />
                </div>
            </div>

            {/* Telefones */}
            <div className="border-t pt-4">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="text-lg font-medium text-gray-900">Telefones</h3>
                    <button
                        type="button"
                        onClick={addTelefone}
                        className="px-3 py-1 text-sm text-indigo-600 hover:text-indigo-900 border border-indigo-600 rounded-md hover:bg-indigo-50"
                    >
                        + Adicionar Telefone
                    </button>
                </div>
                {formData.telefones.map((telefone, index) => (
                    <div key={index} className="flex gap-4 items-end mb-4">
                        <div className="flex-1">
                            <label className="block text-sm font-medium text-gray-700">DDD *</label>
                            <input
                                type="text"
                                value={telefone.ddd}
                                onChange={(e) => handleTelefoneChange(index, 'ddd', e.target.value)}
                                required
                                maxLength={2}
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                            />
                        </div>
                        <div className="flex-1">
                            <label className="block text-sm font-medium text-gray-700">Número *</label>
                            <input
                                type="text"
                                value={telefone.numero}
                                onChange={(e) => handleTelefoneChange(index, 'numero', e.target.value)}
                                required
                                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                            />
                        </div>
                        {formData.telefones.length > 1 && (
                            <button
                                type="button"
                                onClick={() => removeTelefone(index)}
                                className="px-3 py-2 text-sm text-red-600 hover:text-red-900 border border-red-600 rounded-md hover:bg-red-50"
                            >
                                Remover
                            </button>
                        )}
                    </div>
                ))}
            </div>

            <div className="flex justify-end space-x-3 pt-4 border-t">
                <button
                    type="button"
                    onClick={onCancel}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50"
                >
                    Cancelar
                </button>
                <button
                    type="submit"
                    className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 border border-transparent rounded-md hover:bg-indigo-700"
                >
                    {cliente ? 'Atualizar' : 'Criar'}
                </button>
            </div>
        </form>
    );
};
